import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
@Component({
  selector: 'app-add-driver-by-admin',
  templateUrl: './add-driver-by-admin.component.html',
  styleUrls: ['./add-driver-by-admin.component.css']
})
export class AddDriverByAdminComponent implements OnInit {

  constructor(private formBuilder: FormBuilder) { }
  addVechicleForm: FormGroup;
  dynamictype: string = "number";
  post: any = '';
  ngOnInit(): void {
   this. createForm();
  }

  createForm() {
    this.addVechicleForm = this.formBuilder.group({
      'DriverName': [null, Validators.required],
      'DriverName': [null, Validators.required],
      'DriverLicenceNo': [null, Validators.required],
      'DriverNickName': [null, Validators.required],
      'DriverAddress': [null, Validators.required],
      'DriverLicenceIsuedDate': [null, Validators.required],
      'DriverLicenceDOB': [null, Validators.required],
      'DriverLicenceValidForm': [null, Validators.required],
      'DriverLicenceValidUpto': [null, Validators.required],
      'DriverMobileNo': [null, Validators.required],
      'ContractFromDate': [null, Validators.required],
      'ContractTo': [null, Validators.required],
    });
  }
  onSubmit(post) {
    this.post = post;
  }
}
